from .paramdict import ParamDict
from .parameterized import Parameterized

# FIXME: Move suitable parts of functionality from scheme and problem into this class, and refine into a nice interface.
# TODO: Move PostprocessorBase functionality here or the other way

class NSPostProcessor(Parameterized):
    "Baseclass for Navier-Stokes solution postprocessors."
    def __init__(self, params=None):
        Parameterized.__init__(self, params)

    @classmethod
    def default_base_params(cls):
        params = ParamDict(casedir="results")
        return params
