
from .PPField import PPField
from dolfin import *

class FlowRate(PPField):
    def __init__(self, boundary_id, params=None):
        PPField.__init__(self, params)
        self.boundary_id = boundary_id

    @property
    def name(self):
        return "%s_%s" % (self.__class__.__name__, self.boundary_id)

    def compute(self, pp, spaces, problem):
        u = pp.get("Velocity")

        n = problem.mesh.ufl_cell().n
        dsi = problem.ds(self.boundary_id)

        M = dot(u, n)*dsi
        return assemble(M)
