from ppfieldbase import PPFieldBase

class OSI(PPFieldBase):
    def __init__(self, **kwargs):
        # For derived units such as OSI, the keyword "parent" should be included, and referring to a WSS instance
        PPFieldBase.__init__(self, **kwargs)

    def update(self, u, p, t, timestep):
        # Get parent data
        parent_datadict = self.parent.get_data()
        parent_data = parent_datadict["data"]
        parent_last_savetime = parent_datadict["time"]
        parent_last_savetimestep = parent_datadict["timestep"]

        # Perform some logic
        data = None
        self.set_data(t, timestep, data)