from .ppfield import PPField
from ..core.utils import sigma
from dolfin import *

class Stress(PPField):
    def __init__(self, params=None):
        PPField.__init__(self, params)

    def before_first_compute(self, pp, problem): # TODO: Get spaces here
        if self.params.assemble:
            #V = spaces.get_space(0, 2) # TODO: Replace with spaces
            V = TensorFunctionSpace(problem.mesh, "DG", 0)
            self._function = Function(V, name=self.name)
        else:
            pass #V = spaces.DV

    def compute(self, pp, problem):
        u = pp.get("Velocity")
        p = pp.get("Pressure")
        mu = problem.params.mu

        expr = sigma(u, p, mu)
        #u*epsilon(u) - p*Identity(u.cell().d) # TODO: is this with negative pressure?

        return self.expr2function(expr, self._function)
