from .ppfield import PPField
from ..core.utils import sigma
from dolfin import *

class Stress(PPField):
    def __init__(self, params=None):
        PPField.__init__(self, params)

    def before_first_compute(self, pp, problem):
        if self.params.assemble:
            # TODO: Replace with spaces
            DG = TensorFunctionSpace(problem.mesh, "DG", 0)
            self.v = TestFunction(DG)
            self.stress = Function(DG, name="Stress")

    def compute(self, pp, problem):
        u = pp.get("Velocity")
        p = pp.get("Pressure")
        mu = problem.params.mu

        expr = sigma(u, p, mu)

        # TODO: Use helper function for this, repeated in several fields, and use project if necessary
        if self.params.assemble:
            cell = problem.mesh.ufl_cell()
            scale = 1.0/cell.volume
            assemble(scale*inner(expr, self.v)*dx, tensor=self.stress.vector())
            return self.stress
        else:
            return expr
