from ..core.nsscheme import *
from ..core.timesteps import compute_regular_timesteps
from ..core.utils import Timer

class IPCS_p1p1(NSScheme):
    "Incremental pressure-correction scheme, using P1-P1 elements."

    def __init__(self, params):
        NSScheme.__init__(self, params, segregated=False)

    def solve(self, problem):
        # Get problem parameters
        mesh = problem.mesh
        dt, t, timesteps = compute_regular_timesteps(problem)
        dx = problem.dx
        ds = problem.ds

        # Define function spaces
        V = VectorFunctionSpace(mesh, "CG", 1)
        Q = FunctionSpace(mesh, "CG", 1)

        # Get initial and boundary conditions
        ics = problem.initial_conditions(V, Q)
        bcs = problem.boundary_conditions(V, Q, t)
        u0, p0 = ics[:-1], ics[-1]
        bcu, bcp = bcs[:-1], bcs[-1]

        # Remove boundary stress term if problem is periodic
        if is_periodic(bcp):
            beta = Constant(0)
        else:
            beta = Constant(1)

        # Test and trial functions
        v = TestFunction(V)
        q = TestFunction(Q)
        u = TrialFunction(V)
        p = TrialFunction(Q)

        # Functions
        dims = range(len(u0))
        u0 = [interpolate(_u0, V) for _u0 in u0]
        u1 = [Function(V) for _u0 in u0]

        p0 = interpolate(p0, Q)
        p1 = interpolate(p0, Q)
        nu = Constant(problem.nu)
        k  = Constant(dt)
        f  = problem.f
        n  = FacetNormal(mesh)

        # To avoid indexing in non-segregated forms
        u0_ = u0[0]
        u1_ = u1[0]
        f_  = f[0]

        # Tentative velocity step
        u_mean = 0.5 * (u + u0_)
        u_diff = (u - u0_)
        F_u_tent = [(1/k) * inner(v, u_diff) * dx()
                    + inner(v, grad(u0_)*u0_) * dx()
                    + inner(epsilon(v), sigma(u_mean, p0, nu)) * dx()
                    - beta * nu * inner(grad(u_mean).T*n, v) * ds()
                    + inner(v, p0*n) * ds()
                    - inner(v, f_) * dx()]

        a_u_tent = [lhs(F) for F in F_u_tent]
        L_u_tent = [rhs(F) for F in F_u_tent]

        # Pressure correction
        a_p_corr = inner(grad(q), grad(p))*dx()
        L_p_corr = inner(grad(q), grad(p0))*dx() - (1/k)*q*div(u1_)*dx()

        # Velocity correction
        a_u_corr = [inner(v, u)*dx() for r in dims]
        L_u_corr = [inner(v, u1_)*dx() - k*inner(v, grad(p1-p0))*dx()]

        # Assemble matrices
        A_u_tent = [assemble(a) for a in a_u_tent]
        A_p_corr = assemble(a_p_corr)
        A_u_corr = [assemble(a) for a in a_u_corr]

        # Time loop
        timer = Timer(self.params.enable_timer)
        for timestep in xrange(1,len(timesteps)):
            t = timesteps[timestep]

            # Get boundary conditions
            bcs = problem.boundary_conditions(V, Q, t)
            bcu, bcp = bcs[:-1], bcs[-1]
            timer.completed("update & fetch bc")

            # Compute tentative velocity step
            for A, L, u1_comp, bcu_comp in zip(A_u_tent, L_u_tent, u1, bcu):
                b = assemble(L)
                for bc in bcu_comp: bc.apply(A, b)
                timer.completed("u1 construct rhs")
                solver = self.params.solver_u_tent
                iter = solve(A, u1_comp.vector(), b, *solver)
                timer.completed("u1 solve (%s, %d, %d)"%(', '.join(solver), A.size(0), iter))

            # Pressure correction
            b = assemble(L_p_corr)
            if len(bcp) == 0 or is_periodic(bcp):
                solver = self.params.solver_p or self.params.solver_p_neumann
                normalize(b)
            else:
                solver = self.params.solver_p or self.params.solver_p_dirichlet
            for bc in bcp: bc.apply(A_p_corr, b)
            timer.completed("p construct rhs")
            iter = solve(A_p_corr, p1.vector(), b, *solver)
            if len(bcp) == 0 or is_periodic(bcp): normalize(p1.vector())
            timer.completed("p solve (%s, %d, %d)"%(', '.join(solver), A_p_corr.size(0), iter))

            # Velocity correction
            for A, L, u1_comp, bcu_comp in zip(A_u_corr, L_u_corr, u1, bcu):
                b = assemble(L)
                for bc in bcu_comp: bc.apply(A, b)
                timer.completed("u2 construct rhs")
                solver = self.params.solver_u_corr
                iter = solve(A, u1_comp.vector(), b, *solver)
                timer.completed("u2 solve (%s, %d, %d)"%(', '.join(solver), A.size(0),iter))

            # Update
            self.update(problem, t, u1, p1)
            for r in dims: u0[r].assign(u1[r])
            p0.assign(p1)

        return as_object(u1), p1
